package com.RERES.utility;

import java.io.IOException;
import java.util.Base64;
import javax.servlet.ServletException;
import javax.servlet.http.HttpServletResponse;

/**
 *
 * @author ainal farhan
 */
public class ImageUtility {
    public static final String USER_DEFAULT_PROFILE_PICTURE = "";
    
    public static byte[] translateBase64ImageToBytes(String uploadedBase64Image) {
        if(uploadedBase64Image == null) {
            return null;
        }
        else {
            return Base64.getDecoder().decode(uploadedBase64Image);
        }
    }
    
    public static void displaySelectedImage(HttpServletResponse response, String base64Image) 
        throws ServletException, IOException {
        
        byte[] byteData = Base64.getDecoder().decode(base64Image);
        response.setContentType("image/jpeg");
        response.setContentType("image/jpg");
        response.setContentType("image/png");
        
        response.getOutputStream().write(byteData);

        response.getOutputStream().flush();
        response.getOutputStream().close();
    }
}
